// client/src/components/AdminPanel/AdminPanel.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPanel = () => {
    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState('');
    const [productDescription, setProductDescription] = useState('');
    const [productSpecifications, setProductSpecifications] = useState({});
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Fetch products when the component mounts
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/products');
                setProducts(response.data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    const handleAddProduct = async () => {
        try {
            // Create a new product object
            const newProduct = {
                name: productName,
                price: productPrice,
                description: productDescription,
                specifications: productSpecifications,
            };

            // Send a POST request to add the new product
            await axios.post('http://localhost:3001/api/products', newProduct);

            // Refresh the list of products
            const response = await axios.get('http://localhost:3001/api/products');
            setProducts(response.data);

            // Clear the input fields after adding the product
            setProductName('');
            setProductPrice('');
            setProductDescription('');
            setProductSpecifications({});
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    const handleEditProduct = async (productId) => {
        // Implement logic to edit the product based on its ID
        console.log(`Editing product with ID: ${productId}`);
    };

    const handleDeleteProduct = async (productId) => {
        // Implement logic to delete the product based on its ID
        console.log(`Deleting product with ID: ${productId}`);
    };

    return (
        <div className="admin-panel">
            <h2>Административная панель</h2>
            <div className="product-form">
                <h3>Добавить новый товар</h3>
                <label>
                    Название товара:
                    <input type="text" value={productName} onChange={(e) => setProductName(e.target.value)} />
                </label>
                <label>
                    Цена:
                    <input type="text" value={productPrice} onChange={(e) => setProductPrice(e.target.value)} />
                </label>
                <label>
                    Описание:
                    <textarea value={productDescription} onChange={(e) => setProductDescription(e.target.value)} />
                </label>
                <label>
                    Характеристики (JSON):
                    <textarea
                        value={JSON.stringify(productSpecifications)}
                        onChange={(e) => setProductSpecifications(JSON.parse(e.target.value))}
                    />
                </label>
                <button onClick={handleAddProduct}>Добавить товар</button>
            </div>
            <div className="product-list">
                <h3>Существующие товары</h3>
                {products.map((product) => (
                    <div key={product._id} className="product-item">
                        <span>{product.name}</span>
                        <button onClick={() => handleEditProduct(product._id)}>Редактировать</button>
                        <button onClick={() => handleDeleteProduct(product._id)}>Удалить</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AdminPanel;
