import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Sidebar.css';

const Sidebar = () => {
    const [types, setTypes] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3001/api/types')
            .then(response => {
                const uniqueTypes = response.data; // Оставляем только уникальные типы
                setTypes(uniqueTypes);
            })
            .catch(error => console.error('Error fetching types:', error));
    }, []);

    const handleTypeClick = async (type) => {
        try {
            const response = await axios.get(`http://localhost:3001/api/types/${type}`);
            setFilteredProducts(response.data);
        } catch (error) {
            console.error(`Error fetching products of type ${type}:`, error);
        }
    };

    const handleAllProductsClick = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/products');
            setFilteredProducts(response.data);
        } catch (error) {
            console.error('Error fetching all products:', error);
        }
    };

    return (
        <div className="sidebar">
            <h3>Типы товаров</h3>
            <ul>
                <li key="all" onClick={() => handleAllProductsClick()}>Все товары</li>
                {types.map((type, index) => (
                    <li key={index} onClick={() => handleTypeClick(type)}>{type}</li>
                ))}
            </ul>
        </div>
    );
}
export default Sidebar;
