// client/src/components/ProductModal/ProductModal.js
import React, { useEffect, useRef } from 'react';
import './ProductModal.css';

const ProductModal = ({ product, closeModal }) => {
    const modalRef = useRef(null);

    const handleCloseClick = () => {
        closeModal();
    };

    const handleOutsideClick = (event) => {
        if (modalRef.current && !modalRef.current.contains(event.target)) {
            closeModal();
        }
    };

    useEffect(() => {
        // Отключаем прокрутку страницы при открытом модальном окне
        document.body.style.overflow = 'hidden';

        // Добавляем обработчик клика на внешнюю область
        document.addEventListener('click', handleOutsideClick);

        // Восстанавливаем прокрутку и убираем обработчик при закрытии модального окна
        return () => {
            document.body.style.overflow = 'auto';
            document.removeEventListener('click', handleOutsideClick);
        };
    }, []);

    return (
        <div className="modal-overlay">
            <div className="modal-content" ref={modalRef}>
        <span className="close-button" onClick={handleCloseClick}>
          &times;
        </span>
                <h2>{product.name}</h2>
                <div className="modal-details">
                    <img src={product.image} alt={product.name} className="modal-image" />
                    <div className="product-description">
                        <p>{product.description}</p>
                    </div>
                    <h3>Характеристики</h3>
                    <table>
                        <tbody>
                        {Object.entries(product.specifications).map(([key, value]) => (
                            <tr key={key}>
                                <td>{key}</td>
                                <td>{value}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default ProductModal;
