// client/src/components/Sidebar/Sidebar.js

import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Предполагается, что вы установили axios: npm install axios
import './Sidebar.css';

const Sidebar = () => {
    const [types, setTypes] = useState([]);

    useEffect(() => {
        // Получаем уникальные типы товаров из базы данных
        axios.get('/types') // Предполагается, что ваш сервер API слушает '/api/types'
            .then(response => {
                const uniqueTypes = [...new Set(response.data.map(item => item.type))];
                setTypes(uniqueTypes);
            })
            .catch(error => {
                console.error('Ошибка при получении типов товаров', error);
            });
    }, []); // Пустой массив зависимостей, чтобы эффект выполнялся только при монтировании компонента

    return (
        <div className="sidebar">
            <h3>Типы товаров</h3>
            <ul>
                {types.map((type, index) => (
                    <li key={index}>{type}</li>
                ))}
            </ul>
        </div>
    );
};

export default Sidebar;
