// client/src/components/ProductModal/ProductModal.js

import React from 'react';
import './ProductModal.css';

const ProductModal = ({ product, closeModal }) => {
    if (!product || !product.characteristics) {
        return null;
    }

    const { name, description, image, characteristics } = product;

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <span className="close-icon" onClick={closeModal}>&times;</span>
                <div className="modal-header">
                    <img src={image} alt={name} className="modal-image" />
                    <div className="product-info">
                        <h2>{name}</h2>
                        <p>{description}</p>
                    </div>
                </div>
                <div className="modal-body">
                    <h3>Характеристики</h3>
                    <table>
                        <tbody>
                        {typeof characteristics === 'object' && characteristics !== null &&
                        Object.entries(characteristics).map(([key, value]) => (
                            <tr key={key}>
                                <td>{key}</td>
                                <td>{value}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
                <div className="modal-footer">
                    {/* Добавьте дополнительные элементы в подвал модального окна, если необходимо */}
                </div>
            </div>
        </div>
    );
};

export default ProductModal;
