import React, { useState, useEffect } from 'react';
import Header from './components/Header/Header';
import './App.css';
import Sidebar from "./components/Sidebar/Sidebar";
import ProductsList from "./components/ProductsList/ProductsList";
import ProductModal from "./components/ProductModal/ProductModal";
import axios from 'axios';

function App() {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/products');
                setProducts(response.data);
                setFilteredProducts(response.data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    const openModal = (product) => {
        setSelectedProduct(product);
        setIsModalOpen(true);
        console.log( "OPNNNNNN")
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="App">
            <Header setFilteredProducts={setFilteredProducts} allProducts={products} />
            <div className="content">
                <Sidebar setFilteredProducts={setFilteredProducts} products={products} />
                <ProductsList products={filteredProducts} openModal={openModal} />
            </div>
            <ProductModal isOpen={isModalOpen} closeModal={closeModal} product={selectedProduct} />
        </div>
    );
}

export default App;
